import { IExponentialRampToValueAutomationEvent } from 'automation-events/src/interfaces/index';
import { TAutomationEvent } from 'automation-events/src/types/index';

export const isExponentialRampToValueAutomationEvent = (
    automationEvent: TAutomationEvent
): automationEvent is IExponentialRampToValueAutomationEvent => {
    return automationEvent.type === 'exponentialRampToValue';
};
