import { ILinearRampToValueAutomationEvent } from 'automation-events/src/interfaces/index';
import { TAutomationEvent } from 'automation-events/src/types/index';

export const isLinearRampToValueAutomationEvent = (
    automationEvent: TAutomationEvent
): automationEvent is ILinearRampToValueAutomationEvent => {
    return automationEvent.type === 'linearRampToValue';
};
