import { ISetTargetAutomationEvent } from 'automation-events/src/interfaces/index';
import { TAutomationEvent } from 'automation-events/src/types/index';

export const isSetTargetAutomationEvent = (automationEvent: TAutomationEvent): automationEvent is ISetTargetAutomationEvent => {
    return automationEvent.type === 'setTarget';
};
