import { ISetValueCurveAutomationEvent } from 'automation-events/src/interfaces/index';
import { TAutomationEvent } from 'automation-events/src/types/index';

export const isSetValueCurveAutomationEvent = (automationEvent: TAutomationEvent): automationEvent is ISetValueCurveAutomationEvent => {
    return automationEvent.type === 'setValueCurve';
};
