import { isNativeAudioNode } from 'standardized-audio-context/src/guards/native-audio-node';
import { TConnectMultipleOutputsFactory } from 'standardized-audio-context/src/types/index';

export const createConnectMultipleOutputs: TConnectMultipleOutputsFactory = (createIndexSizeError) => {
    return (outputAudioNodes, destination, output = 0, input = 0) => {
        const outputAudioNode = outputAudioNodes[output];

        if (outputAudioNode === undefined) {
            throw createIndexSizeError();
        }

        if (isNativeAudioNode(destination)) {
            return outputAudioNode.connect(destination, 0, input);
        }

        return outputAudioNode.connect(destination, 0);
    };
};
