import { assignNativeAudioNodeOption } from 'standardized-audio-context/src/helpers/assign-native-audio-node-option';
import { assignNativeAudioNodeOptions } from 'standardized-audio-context/src/helpers/assign-native-audio-node-options';
import { testAnalyserNodeGetFloatTimeDomainDataMethodSupport } from 'standardized-audio-context/src/helpers/test-analyser-node-get-float-time-domain-data-method-support';
import { wrapAnalyserNodeGetFloatTimeDomainDataMethod } from 'standardized-audio-context/src/helpers/wrap-analyser-node-get-float-time-domain-data-method';
import { TNativeAnalyserNodeFactoryFactory } from 'standardized-audio-context/src/types/index';

export const createNativeAnalyserNodeFactory: TNativeAnalyserNodeFactoryFactory = (cacheTestResult, createIndexSizeError) => {
    return (nativeContext, options) => {
        const nativeAnalyserNode = nativeContext.createAnalyser();

        // Bug #37: Firefox does not create an AnalyserNode with the default properties.
        assignNativeAudioNodeOptions(nativeAnalyserNode, options);

        // Bug #118: Safari does not throw an error if maxDecibels is not more than minDecibels.
        if (!(options.maxDecibels > options.minDecibels)) {
            throw createIndexSizeError();
        }

        assignNativeAudioNodeOption(nativeAnalyserNode, options, 'fftSize');
        assignNativeAudioNodeOption(nativeAnalyserNode, options, 'maxDecibels');
        assignNativeAudioNodeOption(nativeAnalyserNode, options, 'minDecibels');
        assignNativeAudioNodeOption(nativeAnalyserNode, options, 'smoothingTimeConstant');

        // Bug #36: Safari does not support getFloatTimeDomainData() yet.
        if (
            !cacheTestResult(testAnalyserNodeGetFloatTimeDomainDataMethodSupport, () =>
                testAnalyserNodeGetFloatTimeDomainDataMethodSupport(nativeAnalyserNode)
            )
        ) {
            wrapAnalyserNodeGetFloatTimeDomainDataMethod(nativeAnalyserNode);
        }

        return nativeAnalyserNode;
    };
};
