import { TNativeScriptProcessorNodeFactory } from 'standardized-audio-context/src/types/index';

export const createNativeScriptProcessorNode: TNativeScriptProcessorNodeFactory = (
    nativeContext,
    bufferSize,
    numberOfInputChannels,
    numberOfOutputChannels
) => {
    return nativeContext.createScriptProcessor(bufferSize, numberOfInputChannels, numberOfOutputChannels);
};
