import { IAudioBufferSourceNode, IAudioNode } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/index';

export const isAudioBufferSourceNode = <T extends TContext>(audioNode: IAudioNode<T>): audioNode is IAudioBufferSourceNode<T> => {
    return 'playbackRate' in audioNode;
};
