import { IAudioNode, IAudioParam } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/index';

export const isAudioNode = <T extends TContext>(
    audioNodeOrAudioParam: IAudioNode<T> | IAudioParam
): audioNodeOrAudioParam is IAudioNode<T> => {
    return 'context' in audioNodeOrAudioParam;
};
