import { IAudioNode, IAudioWorkletNode } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/index';

export const isAudioWorkletNode = <T extends TContext>(audioNode: IAudioNode<T>): audioNode is IAudioWorkletNode<T> => {
    return 'port' in audioNode;
};
