import { IAudioNode, IBiquadFilterNode } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/index';

export const isBiquadFilterNode = <T extends TContext>(audioNode: IAudioNode<T>): audioNode is IBiquadFilterNode<T> => {
    return 'frequency' in audioNode && 'gain' in audioNode;
};
