import { IAudioNode, IGainNode } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/index';

export const isGainNode = <T extends TContext>(audioNode: IAudioNode<T>): audioNode is IGainNode<T> => {
    return !('frequency' in audioNode) && 'gain' in audioNode;
};
