import { IAudioNode } from 'standardized-audio-context/src/interfaces/index';
import { TActiveInputConnection, TContext, TPassiveAudioParamInputConnection } from 'standardized-audio-context/src/types/index';
import { insertElementInSet } from 'standardized-audio-context/src/helpers/insert-element-in-set';

export const addActiveInputConnectionToAudioParam = <T extends TContext>(
    activeInputs: Set<TActiveInputConnection<T>>,
    source: IAudioNode<T>,
    [output, eventListener]: TPassiveAudioParamInputConnection,
    ignoreDuplicates: boolean
) => {
    insertElementInSet(
        activeInputs,
        [source, output, eventListener],
        (activeInputConnection) => activeInputConnection[0] === source && activeInputConnection[1] === output,
        ignoreDuplicates
    );
};
