import { IAudioNodeOptions } from 'standardized-audio-context/src/interfaces/index';
import { TNativeAudioNode } from 'standardized-audio-context/src/types/index';
import { assignNativeAudioNodeOption } from 'standardized-audio-context/src/helpers/assign-native-audio-node-option';

export const assignNativeAudioNodeOptions = (nativeAudioNode: TNativeAudioNode, options: IAudioNodeOptions): void => {
    assignNativeAudioNodeOption(nativeAudioNode, options, 'channelCount');
    assignNativeAudioNodeOption(nativeAudioNode, options, 'channelCountMode');
    assignNativeAudioNodeOption(nativeAudioNode, options, 'channelInterpretation');
};
