import { IAudioDestinationNode } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/index';
import { deactivateActiveAudioNodeInputConnections } from 'standardized-audio-context/src/helpers/deactivate-active-audio-node-input-connections';

export const deactivateAudioGraph = <T extends TContext>(context: T): void => {
    deactivateActiveAudioNodeInputConnections(<IAudioDestinationNode<T>>context.destination, []);
};
