import { IAudioNode } from 'standardized-audio-context/src/interfaces/index';
import { TActiveInputConnection, TContext } from 'standardized-audio-context/src/types/index';
import { pickElementFromSet } from 'standardized-audio-context/src/helpers/pick-element-from-set';

export const deleteActiveInputConnectionToAudioParam = <T extends TContext>(
    activeInputs: Set<TActiveInputConnection<T>>,
    source: IAudioNode<T>,
    output: number
) => {
    return pickElementFromSet(
        activeInputs,
        (activeInputConnection) => activeInputConnection[0] === source && activeInputConnection[1] === output
    );
};
