import { IAudioNode } from 'standardized-audio-context/src/interfaces/index';
import { TContext, TInternalStateEventListener } from 'standardized-audio-context/src/types/index';
import { getEventListenersOfAudioNode } from 'standardized-audio-context/src/helpers/get-event-listeners-of-audio-node';

export const deleteEventListenerOfAudioNode = <T extends TContext>(
    audioNode: IAudioNode<T>,
    eventListener: TInternalStateEventListener
) => {
    const eventListeners = getEventListenersOfAudioNode(audioNode);

    if (!eventListeners.delete(eventListener)) {
        throw new Error('Missing the expected event listener.');
    }
};
