import { AUDIO_NODE_CONNECTIONS_STORE } from 'standardized-audio-context/src/globals';
import { IAudioNode } from 'standardized-audio-context/src/interfaces/index';
import { TAudioNodeConnections, TContext, TGetAudioNodeConnectionsFunction } from 'standardized-audio-context/src/types/index';
import { getValueForKey } from 'standardized-audio-context/src/helpers/get-value-for-key';

export const getAudioNodeConnections: TGetAudioNodeConnectionsFunction = <T extends TContext>(
    audioNode: IAudioNode<T>
): TAudioNodeConnections<T> => {
    return <TAudioNodeConnections<T>>getValueForKey(AUDIO_NODE_CONNECTIONS_STORE, audioNode);
};
