import { AUDIO_PARAM_CONNECTIONS_STORE } from 'standardized-audio-context/src/globals';
import { IAudioParam } from 'standardized-audio-context/src/interfaces/index';
import { TAudioParamConnections, TContext, TGetAudioParamConnectionsFunction } from 'standardized-audio-context/src/types/index';
import { getValueForKey } from 'standardized-audio-context/src/helpers/get-value-for-key';

export const getAudioParamConnections: TGetAudioParamConnectionsFunction = <T extends TContext>(
    audioParam: IAudioParam
): TAudioParamConnections<T> => {
    return <TAudioParamConnections<T>>getValueForKey(AUDIO_PARAM_CONNECTIONS_STORE, audioParam);
};
