import { NODE_TO_PROCESSOR_MAPS } from 'standardized-audio-context/src/globals';
import { IAudioNode, IAudioWorkletProcessor } from 'standardized-audio-context/src/interfaces/index';
import { TContext, TNativeAudioWorkletNode, TNativeOfflineAudioContext } from 'standardized-audio-context/src/types/index';
import { getNativeAudioNode } from 'standardized-audio-context/src/helpers/get-native-audio-node';
import { getValueForKey } from 'standardized-audio-context/src/helpers/get-value-for-key';

export const getAudioWorkletProcessor = <T extends TContext>(
    nativeOfflineAudioContext: TNativeOfflineAudioContext,
    proxy: IAudioNode<T>
): Promise<IAudioWorkletProcessor> => {
    const nodeToProcessorMap = getValueForKey(NODE_TO_PROCESSOR_MAPS, nativeOfflineAudioContext);
    const nativeAudioWorkletNode = getNativeAudioNode<T, TNativeAudioWorkletNode>(proxy);

    return getValueForKey(nodeToProcessorMap, nativeAudioWorkletNode);
};
