import { EVENT_LISTENERS } from 'standardized-audio-context/src/globals';
import { TGetEventListenersOfAudioNodeFunction } from 'standardized-audio-context/src/types/index';
import { getValueForKey } from 'standardized-audio-context/src/helpers/get-value-for-key';

export const getEventListenersOfAudioNode: TGetEventListenersOfAudioNodeFunction = (audioNode) => {
    return getValueForKey(EVENT_LISTENERS, audioNode);
};
