import { AUDIO_PARAM_STORE } from 'standardized-audio-context/src/globals';
import { IAudioParam } from 'standardized-audio-context/src/interfaces/index';
import { TNativeAudioParam } from 'standardized-audio-context/src/types/index';
import { getValueForKey } from 'standardized-audio-context/src/helpers/get-value-for-key';

export const getNativeAudioParam = (audioParam: IAudioParam): TNativeAudioParam => {
    return getValueForKey(AUDIO_PARAM_STORE, audioParam);
};
