import { ACTIVE_AUDIO_NODE_STORE } from 'standardized-audio-context/src/globals';
import { TIsPassiveAudioNodeFunction } from 'standardized-audio-context/src/types/index';

export const isPassiveAudioNode: TIsPassiveAudioNodeFunction = (audioNode) => {
    return !ACTIVE_AUDIO_NODE_STORE.has(audioNode);
};
