import { TAnyAudioBuffer, TContext } from 'standardized-audio-context/src/types/index';
import { IAudioParam } from 'standardized-audio-context/src/interfaces/audio-param';
import { IAudioScheduledSourceNode } from 'standardized-audio-context/src/interfaces/audio-scheduled-source-node';

export interface IAudioBufferSourceNode<T extends TContext> extends IAudioScheduledSourceNode<T> {
    buffer: null | TAnyAudioBuffer;

    /*
     * Bug #149: Safari does not yet support the detune AudioParam.
     *
     * readonly detune: IAudioParam;
     */

    loop: boolean;

    loopEnd: number;

    loopStart: number;

    readonly playbackRate: IAudioParam;

    start(when?: number, offset?: number, duration?: number): void;
}
