import { IAudioNode, IAudioNodeRenderer, IMinimalOfflineAudioContext, IOfflineAudioContext } from 'standardized-audio-context/src/interfaces/index';
import { TContext } from 'standardized-audio-context/src/types/context';

export type TAudioNodeRenderer<T extends TContext, U extends IAudioNode<T> = IAudioNode<T>> = T extends
    | IMinimalOfflineAudioContext
    | IOfflineAudioContext
    ? IAudioNodeRenderer<T, U>
    : null;
